import React from 'react'
import { useState } from 'react';

export const AddTodo = (props,todo, onAdd) => {
    const [title, setTitle] = useState("");
    const [desc, setDesc] = useState("");
    const submit=(e)=>{
        e.preventDefault();
        if(!title){
            alert("Title can not be blank")
        }
        props.addTodo(title,desc);
    }

    return (
        <div className='text-center container my-3'>
            <h3>Add a Todo</h3>
            <form onSubmit={submit}>
                <div class="mb-1">
                    <label for="title" class="form-label">Task</label>
                    <input type="text" value={title} onChange={(e)=>{setTitle(e.target.value)}} class="form-control" id="title" aria-describedby="emailHelp" />
                </div>
                <div class="mb-1 " >
                    <label for="desc" class="form-label">Task Description</label>
                    <input type="text" value={desc} onChange={(e)=>{setDesc(e.target.value)}} class="form-control" id="desc" />
                </div>

                <button type="submit" class="btn btn-success btn-sm" onClick={()=>{onAdd(todo)}}>Add Todo</button>
            </form>
        </div>
    )
}


