import './App.css';
import Header from "./MyComponents/Header" //name
import {Todos} from "./MyComponents/Todos" //constant
import {Footer} from "./MyComponents/Footer" //constant
import React, { useState } from 'react'; 
import {AddTodo} from "./MyComponents/AddTodo"
function App() {


  const onDelete=(todo)=>{
    console.log("I am Deleted!!! ",todo);
    // Deleting this way in react doesn't work!!!!
    // let index=todos.indexOf(todo);
    // todos.splice(index,1);
    setTodos(todos.filter((e)=>{
        return e!=todo;
    }))
  }

  const addTodo=(title,desc)=>{
      console.log("adding this todo",title, desc)
      let sno=todos.length+1
      const myTodo={
        sno:sno,
        title:title,
        desc:desc,
      }
  }

  const [todos, setTodos] = useState([
    {
      sno:1,
    title:"First todo",
    desc:"First description here"
  },
    {
      sno:2,
      title:"Second todo",
      desc:"Second description here"
    },
      {
        sno:3,
        title:"Third todo",
        desc:"Third description here"
      },
  ]);


  return (
    <>
      <Header title="My Todos List" searchBar={false}/>
      <AddTodo addTodo={addTodo}/>
      <Todos todos={todos} onDelete={onDelete}/>

      <Footer/>
    </>

  );

}

export default App;
